import React, { useReducer, useEffect } from "react"
import firebase from './utils/Firebase'


const Context = React.createContext()

function ContextProvider({ children }) {
    const [shoes, setShoes] = useReducer((state, newState) => ({ ...state, ...newState }),
        {
        amortecimento: "",
        drop: "",
        peso: "",
        treino: ""
    })


    const handleChange = evt => {
        const { name, value } = evt.target;

        setShoes({ [name]: value })
        console.log(shoes)
    }


    const addItemToDb = (amortecimento, drop, peso, treino) => {
        firebase.firestore().collection('Teste').add({
            amortecimento: amortecimento,
            drop: drop,
            peso: peso,
            treino: treino
        })
    }


    return (
        <Context.Provider value={{ addItemToDb, handleChange }}>
            {children}
        </Context.Provider>
    )
}

export { ContextProvider, Context }
