import React, {useContext} from 'react'
import Selects from '../components/Selects'
import Input from '../components/Input'
import {Context} from '../Context'


export default function AddItem() {

    const {addItemToDb} = useContext(Context)

    const amortecimentoOptions = [
        {value: 'Leve', label: 'Leve',},
        {value: 'Macio', label: 'Macio'},
        {value: 'Extra Macio', label: 'Extra Macio'}

    ]
   const tipoDeTreinoOptions = [
       {value: 'Rodagem', label: 'Rodagem' },
       {value: 'Tiro', label: 'Tiro' },
       {value: 'Competição', label: 'Competição' }

   ]
    return (
        <div>
            <h1>'Admin' page to add the shoes to the DB</h1>
            <h3>Shoes info</h3>
            <Input type="number" min="4" max="12" placeholder="Drop" />
            <Input type="number" placeholder="peso" />
            <Selects options={amortecimentoOptions} />
            <Selects options={tipoDeTreinoOptions} />
            <button onClick={addItemToDb('1', '2', 3, 4)}></button>
        </div>
    )
}




