import React, {useContext} from 'react'
import Select from 'react-select'
import { Context } from '../Context'

export default function Selects({options, name}) {
    const {handleChange, handleOptionChange} = useContext(Context)
    return (
       <Select options ={options} onChange={handleOptionChange} name={name} />
    )
}
