import React, { useState, useEffect } from "react"
import firebase from './utils/Firebase'


const Context = React.createContext()

function ContextProvider({ children }) {
    const [amortecimento, setAmortencimento] = useState([])

    function handleChange(e) {
        const value = e.value
        setAmortencimento(prevValue => [...prevValue, value])
        console.log(amortecimento)
    }


// [13:03, 04/05/2020] Paulo: Amortecimento: Leve, Macio, Extra Macio
// Dropp: pode ser entre 4, 6,8,10,12 (milímetros)
// [13:03, 04/05/2020] Paulo: *drop
// [13:04, 04/05/2020] Paulo: Peso (gramas)
// [13:05, 04/05/2020] Paulo: Tipo de treino: rodagem , tiro / competição


    const addItemToDb = (amortecimento, drop, peso, treino) => {
        firebase.firestore().collection('Teste').add({
            amortecimento: amortecimento,
            drop: drop,
            peso: peso,
            treino: treino
        })
    }


    return (
        <Context.Provider value={{ addItemToDb, handleChange }}>
            {children}
        </Context.Provider>
    )
}

export { ContextProvider, Context }
