import React, { useState, useEffect } from "react"
import firebase from './utils/Firebase'


const Context = React.createContext()

function ContextProvider({ children }) {
    const [amortecimento, setAmortencimento] = useState([])
    function handleChange(e) {
        const value = e.value
        setAmortencimento(prevValue => [...prevValue,value])
        console.log(amortecimento)
    }

    const addItemToDb = (title, postedby, time) => {
        firebase.firestore().collection('Teste').add({
            tittle: title ,
            postedby: postedby,
            time: time
        })
    }


    return (
        <Context.Provider value={{addItemToDb, handleChange}}>
            {children}
        </Context.Provider>
    )
}

export { ContextProvider, Context }
