    useEffect(() => {
      const fetchData = async () => {
      const db = Firebase.firestore()
      const data = await db.collection('Tenis').get().then(snapshot => {
        snapshot
          .docs
          .forEach(doc => {
            console.log(JSON.parse(doc._document.data.toString()))
          });
      });
       setTenis(data.docs)
    }
    fetchData();
    console.log(tenis)
  }, [])

    return (
      <div>
        {tenis.map(item => (
          <li key={item.name}>
            {item.name}
          </li>
       ))}
      </div>
    );
}
