import React, { useReducer, useEffect, useState } from "react"
import firebase from './utils/Firebase'


const Context = React.createContext()

function ContextProvider({ children }) {
    const [shoes, setShoes] = useState(
        {
            name: "padrao",
            drop: "padrao",
            peso: "padrao",
            amortecimento: "padrao",
            treino: "padrao"
        }
    )

        const handleChange = (name) => (e) => {
            const newShoes = {
                ...shoes,
                [propertyName]: e.target.value
            }
            setShoes(newShoes)
            console.log(shoes)
        }


         const addItemToDb = (shoes) => {
        firebase.firestore().collection('Teste').add({
            name: shoes.name,
            amortecimento: shoes.amortecimento,
            drop: shoes.drop,
            peso: shoes.peso,
            treino: shoes.treino
        })
    }


    return (
        <Context.Provider value={{ addItemToDb, handleChange }}>
            {children}
        </Context.Provider>
    )
}

export { ContextProvider, Context }
